mapper.select:
SELECT id, answer_user_id, answer, answer_time
FROM answer
WHERE id = #{id,jdbcType=INTEGER}

mapper.update:
UPDATE answer
SET answer_user_id = #{answerUserId,jdbcType=VARCHAR},
    answer = #{answer,jdbcType=LONGVARCHAR},
    answer_time = #{answerTime,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=INTEGER}
